mapper.select:
SELECT member_id, member_pw 
 FROM member
 WHERE member_id = #{member_id} AND member_pw = #{member_pw}

mapper.delete:
DELETE FROM member
WHERE member_id = #{member_id}
  AND member_pw = #{member_pw}

mapper.select:
SELECT profile_name, profile_intro
FROM profile
WHERE member_id = #{member_id}
